import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-two',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './two.component.html',
  styleUrls: ['./two.component.css']
})
export class TwoComponent {

  text:any

}
<div class="row mb-3 justify-content-center">
  <h2 class="text-center">Two Way Data Binding</h2>
  <div class="col-6 mt-3">
    <label for="inpText" class="form-label">Input Text</label>
    <input #inpText [value]="inpText.value"
        (input)="text = inpText.value" type="text"
        name="inpText" id="inpText" class="form-control mb-3">

    <p>{{ text }}</p>
  </div>
</div>
