import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styles: []
})
export class AppComponent {
  title = 'The Component'

  parentCount:number = 0

  countUp() {
    this.parentCount ++
  }
}
<div class="container mt-4">

  <h2 class="text-seconday">{{ title }}</h2>

  <div class="row">

    <app-child [childCount]="parentCount"></app-child>

  </div>

  <div class="row">
    <button (click)="countUp()" class="btn btn-outlined-primary">Count Up</button>
  </div>

</div>
