import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-interpo',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './interpo.component.html',
  styleUrls: ['./interpo.component.css']
})
export class InterpoComponent {

  value:any

  add(value:any) {
    this.value = value
  }

}
<div class="row mb-3 align-items-center">

  <div class="col">
    <label for="text" class="form-label">Some Text</label>
    <input #text type="text" class="form-control mb-3" id="text" placeholder="Enter text">
    <input type="button" value="Click" class="btn btn-outline-success" (click)="add(text.value)">
  </div>

  <div class="col">
    <div class="alert alert-success">{{ value ? value : 'There is no data' }}</div>
  </div>

</div>
